import React, { Component } from 'react'
import Header from '../../components/header/Header'
import Stepper from '../../components/stepper/Stepper'
import './Signup.css'
import UserTypeImg from '../../images/user-type.png'

class Signup extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             userTypeState: ""
        }
    }
    

    onUserTypeClick(n){
        console.log('button clicked '+n)
        var userType = document.getElementsByClassName("user-types")[0].children[n];
        console.log(userType);
    }

    render() {
        return (
            <div>
                <Header />
                <div id="signup-container">
                    <h2>Sign Up</h2>
                    <Stepper maxNumber={4} activeNumber={1} />
                    <form className="form">
                        <div className="labeled-input"><label>Email </label><input type="email" /></div>
                        <div className="labeled-input"><label>Password </label><input type="password" /></div>
                        <div className="labeled-input"><label>Confirm Password </label><input type="password" /></div>
                    </form>
                    <div id="signup-bottom">
                        <h2>Choose your user type</h2>
                        <ul className="user-types">
                            <li className="user-type">
                                <a href="#" onClick={() => this.onUserTypeClick(1)}><img src={UserTypeImg}/></a>
                            </li>
                            <li className="user-type">
                                <a href="#" onClick={() => this.onUserTypeClick(2)}><img src={UserTypeImg}/></a>
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
        )
    }
}

export default Signup
