import React, { Component } from 'react'

export default class Login extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             message: ""
        }
    }

    onLogin = () =>{
        this.setState({
            message: "Logged In"
        });
    }
    
    render() {
        return (
            <div>
                <h2>{this.state.message}</h2>
                <input placeholder="username"/>
                <input placeholder="password"/>
                <button onClick={onLogin}>Login</button>
            </div>
        )
    }
}
