import React, { Component } from 'react'
import Header from '../../components/header/Header'
import Signup from '../signup/Signup'
import './Login.css'

class Login extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             email: "",
             password: "",
        }
    }

    handleChange(event){
        const value = event.target.value;
        this.setState({
            [event.target.name]: value
        })
    }
    
    render() {
        return (
            <div>
                <Header />
                <div id="login-body">
                    <h2>Login</h2>
                    <form className="form">
                        <div className="labeled-input"><label>Email</label><input type="email" name="email" value={this.state.email} onChange={this.handleChange.bind(this)} /></div>
                        <div className="labeled-input"><label>Password</label><input type="password" name="email" value={this.state.email} onChange={this.handleChange.bind(this)} /></div>
                        <div className="forgotpassword"><a href="#!">Forgot Password?</a></div>
                        <button>Login</button>
                    </form>
                </div>
                <div className="login-footer">
                    <p>Don't have an account? <a href="#!" linkTo={Signup}>Sign Up</a></p>
                </div>
            </div>
        )
    }
}

export default Login
