import React, { Component } from 'react'
import Header from '../../components/header/Header'
import Stepper from '../../components/stepper/Stepper'
import './Signup.css'
import UserTypeImg from '../../images/user-type.png'

class Signup extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             selectedUserType: 1,
             stepNumber: 1,
             maxStepper: 4
        }
    }
    

    onUserTypeClick(n){
        this.setState({
            selectedUserType: n
        })
        console.log(this.state.selectedUserType)
    }

    onNextClick(){
        if(this.state.stepNumber < this.state.maxStepper){
            this.setState({
                stepNumber: this.state.stepNumber+1
            })
        }
    }

    onPrevClick(){
        if(this.state.stepNumber > 1){
            this.setState({
                stepNumber: this.state.stepNumber-1
            })
        }
    }

    render() {
        let signupBody
        if(this.state.stepNumber == 1){
            signupBody = <div>
                <form className="form">
                    <div className="labeled-input"><label>Email </label><input type="email" /></div>
                    <div className="labeled-input"><label>Password </label><input type="password" /></div>
                    <div className="labeled-input"><label>Confirm Password </label><input type="password" /></div>
                </form>
                <div id="signup-bottom">
                    <h2>Choose your user type</h2>
                    <ul className="user-types">
                        <li className={this.state.selectedUserType == 1 ? 'user-type active' : 'user-type'}>
                            <a href="javascript:void(0);" onClick={() => this.onUserTypeClick(1)}><img src={UserTypeImg}/></a>
                            <p>Student</p>
                        </li>
                        <li className={this.state.selectedUserType == 2 ? 'user-type active' : 'user-type'}>
                            <a href="javascript:void(0);" onClick={() => this.onUserTypeClick(2)}><img src={UserTypeImg}/></a>
                            <p>Teacher</p>
                        </li>
                    </ul>
                    
                </div>
            </div>
        }
        else if(this.state.stepNumber == 2){
            signupBody = <div>
                <form className="form">
                    <h2>Basic Details</h2>
                    <div className="labeled-input"><label>First Name </label><input type="text" /></div>
                    <div className="labeled-input"><label>Last Name </label><input type="text" /></div>
                    <div className="labeled-input"><label>Birth Date </label><input type="date" /></div>
                    <div className="labeled-input"><label>Contact Number </label><input type="text" /></div>
                    <div className="labeled-input"><label>Address </label><input type="text" /></div>
                    <div className="labeled-input">
                        <label>Education Level </label>
                        <select type="text">
                            <option>option 1</option>
                            <option>option 2</option>
                            <option>option 3</option>
                        </select>
                    </div>
                </form>
            </div>
        }
        return (
            <div>
                <Header />
                <div id="signup-container">
                    <h2>Sign Up</h2>
                    <Stepper maxNumber={this.state.maxStepper} activeNumber={this.state.stepNumber} />
                    {signupBody}
                    <div className="footer-btn">
                        <a className="btn btn-secondary" href="javascript:void(0);" onClick={this.onPrevClick.bind(this)}>{this.state.stepNumber == 1 ? 'Cancel' : 'Previous'}</a>
                        <a className="btn btn-primary" href="javascript:void(0);" onClick={this.onNextClick.bind(this)}>Next</a>
                    </div>
                </div>
            </div>
        )
    }
}

export default Signup
